import React, { Component } from 'react';

class UserPage extends Component {
  state = {
    users: [],
    username: "",
    syncs: []
  }

  componentDidMount(){
    fetch("/users")
      .then(res => res.json())
      .then(users => this.setState({ users }))
  }

  onSubmit = async () => {
    const res = await fetch("/users/sync",
                          { method:"POST",
                            mode:'cors',
                            headers: {
                              'Content-Type':'application/json'
                            },
                            body: JSON.stringify({user: this.state.username})
                          });
    const data = res.json();
    this.setState({ syncs: data.sync });
    console.log(data);
  }

  render() {
    return (
      <div>
        <h1>Users</h1>
        {this.state.users.map(user =>
          <div>
            <h2 key={user.id}>{user.username}</h2>
            <ul>
              <li key={user.id}>{user.spotifyId}</li>
              <li key={user.id}>{user.email}</li>
            </ul>
          </div>
        )}
        <form method="POST">
          <label for="syncUser">Enter user to sync with: </label>
          <input
            name="syncUser"
            placeholder="username"
            value={this.state.username}
            onChange={e => this.setState({username: e.target.value})}
          />
          <button onClick={() => this.onSubmit()}>Sync!</button>
        </form>
        <p class="sync">{this.state.syncs}</p>
      </div>
    );
  }

}

export default UserPage;
