import React, { Component } from 'react';

class Sync extends Component {
  state = {
    username: "tedim52",
    sync: []
  }

  render() {
    return (
      <div>
        <form>
          <label for="syncUser">Enter user to sync with: </label>
          <input
            name="syncUser"
            placeholder="username"
            value={this.state.username}
            onChange={e =>
              fetch('/users/sync', {
                method: "POST",
                body: JSON.stringify({user: e.target.value})
              })
              .then(res => res.json())
              .then(sync => this.setState({ sync: sync }))
            }
          />
        </form>
      </div>
    );
  }

}

export default Sync;
