import React, { Component } from 'react';
import Sync from './Sync'
class UserPage extends Component {
  state = {
    users: [],
    username: "",
    sync: []
  }

  onSubmit = async () => {
    const res = await fetch("http:localhost/8080/users/sync",
                          { method:"POST",
                            body: JSON.stringify({user: this.state.username})
                          }).catch(e => console.log(e));
    const data = res.json();
    this.setState({ sync: data.sync });
    console.log(data);
  }

  render() {
    return (
      <div>
        <h1>Users</h1>
        <form method="POST">
          <label for="syncUser">Enter user to sync with: </label>
          <input
            name="syncUser"
            placeholder="username"
            value={this.state.username}
            onChange={e => this.setState({username: e.target.value})}
          />
          <button onClick={() => this.onSubmit()}>Sync!</button>
        </form>
        <div>
          <h2>Sync</h2>
          <Sync username={this.state.username}/>
        </div>
      </div>
    );
  }

}

export default UserPage;
